#[cfg(feature = "ml-dsa-44")]
use fips204::ml_dsa_44;
#[cfg(feature = "ml-dsa-44")]
use fips204::traits::{KeyGen, SerDes, Signer, Verifier};
#[cfg(feature = "ml-dsa-44")]
use rand_chacha::rand_core::SeedableRng;


#[cfg(feature = "ml-dsa-44")]
#[test]
fn test_browser_message() {
    let msg = b"asdf";
    let mut rng = rand_chacha::ChaCha8Rng::seed_from_u64(123);
    let (pk, sk) = ml_dsa_44::KG::try_keygen_with_rng(&mut rng).unwrap();
    let sig = sk.try_sign_with_rng(&mut rng, msg, &[]).unwrap();
    assert!(pk.verify(msg, &sig, &[]));

    assert_eq!(sk.into_bytes(), *hex::decode("").unwrap(), "sk not correct");
    assert_eq!(sig, *hex::decode("").unwrap(), "sig not correct");
    assert_eq!(pk.into_bytes(), *hex::decode("").unwrap(), "pk not correct");
}
